import React from "react";

export default function TaskCard({ id, title, dueDate, completed, onDelete }) {
  const now = new Date();
  const due = dueDate ? new Date(dueDate) : null;

  let status = "⏳ Pending";
  if (completed) status = "✅ Completed";
  else if (due && due < now) status = "❌ Missed";

  return (
    <div className="border border-gray-700 rounded p-4 bg-white text-black shadow relative">
      <h3 className="font-semibold text-lg">{title}</h3>
      <p>Status: {status}</p>
      {due && (
        <p className="text-sm text-gray-600">Due: {due.toLocaleString()}</p>
      )}
      <button
        onClick={() => onDelete(id)}
        className="absolute top-2 right-2 text-red-600 hover:text-red-800 text-sm"
      >
        ✖
      </button>
    </div>
  );
}
