import React, { useState } from "react";

export default function TaskCard({ id, title, dueDate, completed, onDelete, onToggleComplete }) {
  const now = new Date();
  const due = dueDate ? new Date(dueDate) : null;

  let status = "⏳ Pending";
  if (completed) status = "✅ Completed";
  else if (due && due < now) status = "❌ Missed";

  return (
    <div className="bg-white text-black rounded p-4 shadow-md relative min-h-[120px] flex flex-col justify-between">
      <div className="flex items-center mb-2">
        <input
          type="checkbox"
          checked={completed}
          onChange={() => onToggleComplete(id, !completed)}
          className="mr-3 w-5 h-5"
          title={completed ? "Mark as incomplete" : "Mark as completed"}
        />
        <h3 className={`font-medium text-lg ${completed ? "line-through text-gray-500" : ""}`}>
          {title}
        </h3>
      </div>

      {due && (
        <p className="text-sm text-gray-600 mb-1">
          Due: {due.toLocaleString()}
        </p>
      )}
      <p className="text-sm text-gray-700">Status: {status}</p>

      <button
        onClick={() => onDelete(id)}
        title="Delete"
        className="absolute top-2 right-2 text-gray-400 hover:text-red-600 text-sm"
      >
        ✖
      </button>
    </div>
  );
}
