import React, { useEffect, useState } from "react";
import TaskCard from "./components/TaskCard";

export default function App() {
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState("");
  const [date, setDate] = useState(""); // for date only
  const [time, setTime] = useState(""); // for time only

  const fetchTasks = async () => {
    try {
      const res = await fetch("http://localhost:5000/tasks");
      const data = await res.json();
      setTasks(data);
    } catch (err) {
      console.error("Error fetching tasks:", err);
    }
  };

  useEffect(() => {
    fetchTasks();
  }, []);

  // Combine date + time into ISO string for dueDate
  const addTask = async (e) => {
    e.preventDefault();
    if (!title.trim()) return;

    let dueDate = "";
    if (date) {
      dueDate = time ? `${date}T${time}` : `${date}T00:00`;
    }

    await fetch("http://localhost:5000/tasks", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ title, dueDate }),
    });

    setTitle("");
    setDate("");
    setTime("");
    fetchTasks();
  };

  // ... deleteTask, toggleComplete remain same

  return (
    <div className="min-h-screen bg-black text-white p-6">
      <h1 className="text-4xl font-bold mb-8">Notes & Tasks</h1>

      <form
        onSubmit={addTask}
        className="bg-white rounded p-4 max-w-xl mb-6 shadow-md"
      >
        <input
          type="text"
          placeholder="Take a note..."
          className="w-full text-black text-lg mb-4 outline-none"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />

        <div className="flex gap-4 mb-4">
          <input
            type="date"
            className="text-black p-2 rounded flex-1"
            value={date}
            onChange={(e) => setDate(e.target.value)}
          />
          <input
            type="time"
            className="text-black p-2 rounded flex-1"
            value={time}
            onChange={(e) => setTime(e.target.value)}
          />
        </div>

        <button
          type="submit"
          className="bg-black text-white px-6 py-2 rounded hover:bg-gray-800 text-sm font-semibold"
        >
          Add
        </button>
      </form>

      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4">
        {tasks.length === 0 && <p className="text-gray-400">No tasks yet.</p>}
        {tasks.map((task) => (
          <TaskCard
            key={task.id}
            {...task}
            onDelete={deleteTask}
            onToggleComplete={toggleComplete}
          />
        ))}
      </div>
    </div>
  );
}
