import React from "react";

export default function TaskCard({ id, title, dueDate, completed, onDelete }) {
  const now = new Date();
  const due = dueDate ? new Date(dueDate) : null;

  let status = "⏳ Pending";
  if (completed) status = "✅ Completed";
  else if (due && due < now) status = "❌ Missed";

  return (
    <div className="bg-white text-black rounded p-4 shadow-md relative min-h-[120px]">
      <h3 className="font-medium text-lg mb-2">{title}</h3>
      {due && (
        <p className="text-sm text-gray-600 mb-1">
          Due: {new Date(due).toLocaleString()}
        </p>
      )}
      <p className="text-sm text-gray-700">Status: {status}</p>
      <button
        onClick={() => onDelete(id)}
        title="Delete"
        className="absolute top-2 right-2 text-gray-400 hover:text-red-600 text-sm"
      >
        ✖
      </button>
    </div>
  );
}
