import React, { useEffect, useState } from "react";
import TaskCard from "./components/TaskCard";

export default function App() {
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState("");
  const [dueDate, setDueDate] = useState("");

  const fetchTasks = async () => {
    try {
      const res = await fetch("http://localhost:5000/tasks");
      const data = await res.json();
      setTasks(data);
    } catch (err) {
      console.error("Error fetching tasks:", err);
    }
  };

  useEffect(() => {
    fetchTasks();
  }, []);

  const addTask = async (e) => {
    e.preventDefault();
    if (!title.trim()) return;

    await fetch("http://localhost:5000/tasks", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ title, dueDate }),
    });

    setTitle("");
    setDueDate("");
    fetchTasks();
  };

  const deleteTask = async (id) => {
    await fetch(`http://localhost:5000/tasks/${id}`, {
      method: "DELETE",
    });
    fetchTasks();
  };

  return (
    <div className="min-h-screen bg-black text-white p-6">
      <h1 className="text-4xl font-bold mb-8">Notes & Tasks</h1>

      <form
        onSubmit={addTask}
        className="bg-white rounded p-4 max-w-xl mb-6 shadow-md"
      >
        <input
          type="text"
          placeholder="Take a note..."
          className="w-full text-black text-lg mb-2 outline-none"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <div className="flex justify-between items-center">
          <input
            type="datetime-local"
            className="text-black text-sm"
            value={dueDate}
            onChange={(e) => setDueDate(e.target.value)}
          />
          <button
            type="submit"
            className="bg-black text-white px-4 py-1 rounded hover:bg-gray-800 text-sm"
          >
            Add
          </button>
        </div>
      </form>

      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4">
        {tasks.length === 0 && <p className="text-gray-400">No tasks yet.</p>}
        {tasks.map((task) => (
          <TaskCard key={task.id} {...task} onDelete={deleteTask} />
        ))}
      </div>
    </div>
  );
}
